import sampleProducts from "../data/sampleProducts";
import { Link } from "react-router-dom";

const TopSellingCarousel = () => {
  // En çok satan 3 ürünü sırala
  const topProducts = [...sampleProducts]
    .sort((a, b) => b.sold - a.sold)
    .slice(0, 3);

  return (
    <div id="topSellingCarousel" className="carousel slide mb-5" data-bs-ride="carousel">
      <div className="carousel-inner rounded shadow-lg">
        {topProducts.map((product, index) => (
          <div
            key={product.id}
            className={`carousel-item ${index === 0 ? "active" : ""}`}
          >
            <img
              src={product.image}
              className="d-block w-100"
              alt={product.name}
              style={{
                height: "500px",
                objectFit: "cover",
                filter: "brightness(70%)",
              }}
            />
            <div className="carousel-caption d-none d-md-block">
              <h2 className="fw-bold">{product.name}</h2>
              <p className="fs-5">{product.description}</p>
              <Link
                to={`/product/${product.id}`}
                className="btn btn-lg btn-primary fw-semibold shadow"
              >
                Ürünü İncele
              </Link>
            </div>
          </div>
        ))}
      </div>

      {/* Slider Kontrolleri */}
      <button
        className="carousel-control-prev"
        type="button"
        data-bs-target="#topSellingCarousel"
        data-bs-slide="prev"
      >
        <span className="carousel-control-prev-icon" />
      </button>
      <button
        className="carousel-control-next"
        type="button"
        data-bs-target="#topSellingCarousel"
        data-bs-slide="next"
      >
        <span className="carousel-control-next-icon" />
      </button>
    </div>
  );
};

export default TopSellingCarousel;
